import React, { Component } from 'react';
import * as ROSLIB from 'roslib';

class RawImageDisplay extends Component {
  state = {
    ros: null,
    imageData: null,
  };

  componentDidMount() {
    this.initConnection();
    this.subscribeToRawImage();

  }

  initConnection() {
    const ros = new ROSLIB.Ros();

    ros.on('error', (error) => {
      console.error('ROS Error:', error);
    });

    try {
      ros.connect('ws://localhost:9090'); // Update with your ROS Bridge server URL
    } catch (error) {
      console.error('Cannot connect to the WS robot. Try again after 1 second');
    }

    this.setState({ ros });
  }

  subscribeToRawImage() {
    const { ros } = this.state;

    if (ros) {
      const imageTopic = new ROSLIB.Topic({
        ros,
        name: '/camera/image_raw',
        messageType: 'sensor_msgs/Image',
      });

      imageTopic.subscribe((message) => {
        // Assuming the raw image data is in the 'data' field of the 'message'
        const imageData = `data:image/jpeg;base64,${message.data}`;

        // Set the source of the image element
        this.setState({ imageData });
      });
    }
  }

  render() {
    const { imageData } = this.state;
    return (
      <div>
        <div>
          <img src={imageData} alt="Raw Image" />
        </div>
      </div>
    );
  }
}

export default RawImageDisplay;
