import React, { Component } from 'react';
import { Row, Col } from 'react-bootstrap';
import Config from '../scripts/config';
import * as ROSLIB from 'roslib';


class RobotState extends Component {
  state = {
    ros: null,
    robotPosition: null,
  };

  componentDidMount() {
    this.initConnection();
    this.viewMap();
    this.subscribeToRobotPose();
  }

  initConnection() {
    try {
      const ros = new ROSLIB.Ros();
      ros.on('error', (error) => {
        console.error('ROS Error:', error);
      });

      ros.connect(`ws://${Config.ROSBRIDGE_SERVER_IP}:${Config.ROSBRIDGE_SERVER_PORT}`);
      this.setState({ ros });
    } catch (error) {
      console.error(`Cannot connect to the WS robot: ${error}`);
    }
  }

  subscribeToRobotPose() {
    const { ros } = this.state;

    if (ros) {
      const robotPoseTopic = new ROSLIB.Topic({
        ros,
        name: '/robot_pose',
        messageType: 'geometry_msgs/Pose',
      });

      robotPoseTopic.subscribe((message) => {
        // Update state with the received robot position
        this.setState({ robotPosition: message });
      });
    }
  }

  viewMap() {
    const { ros } = this.state;

    if (ros) {
      const viewer = new window.ROS2D.Viewer({
        divID: 'nav_div3',
        width: 200,
        height: 200,
      });

      const arrow = new window.ROS2D.Arrow({
        size: 10,
        strokeSize: 1,
        fillColor: createjs.Graphics.getRGB(255, 0, 0),
      });

      // Add the arrow to the viewer's scene
      viewer.scene.addChild(arrow);

      const navClient = new ROS2D.OccupancyGridClientNav({
        ros,
        rootObject: viewer.scene,
        viewer,
        serverName: '/move_base',
        withOrientation: true,
      });

      // Update the arrow's position on each animation frame
      viewer.addObject(arrow);
      viewer.animate();

      viewer.on('frameRendered', () => {
        const { robotPosition } = this.state;

        if (robotPosition) {
          // Update arrow's position based on the robot position
          const pose = robotPosition.position;
          arrow.x = pose.x;
          arrow.y = pose.y;
          arrow.rotation = Math.atan2(robotPosition.orientation.z, robotPosition.orientation.w) * (180 / Math.PI);
        }
      });
    }
  }

  render() {
    return (
      <div>
        <div id="nav_div3"></div>
      </div>
    );
  }
}

export default RobotState;
