import React, { useEffect } from 'react';
import ROSLIB from 'roslib';

const RobotControlComponent = () => {
  useEffect(() => {
    // Initialize ROS
    const ros = new ROSLIB.Ros({
      url: 'ws://localhost:9090', // Replace with your ROS2 WebSocket Bridge URL
    });

    // Raw image topic
    const rawImageTopic = new ROSLIB.Topic({
      ros: ros,
      name: '/raw_image_topic',
      messageType: 'sensor_msgs/Image', // Adjust the message type based on your topic
    });

    rawImageTopic.subscribe((message) => {
      const imageData = 'data:image/jpeg;base64,' + btoa(String.fromCharCode.apply(null, new Uint8Array(message.data)));
      const displayImage = document.getElementById('raw_image_display');
      displayImage.setAttribute('src', imageData);
    });

    // Cleanup on component unmount
    return () => {
      rawImageTopic.unsubscribe();
    };
  }, []);

  return (
    <div>
      <h1>ROS2 Robot Control</h1>
      <img id="raw_image_display" alt="Raw Image" />
    </div>
  );
};

export default RobotControlComponent;
