import React, { useEffect, useRef } from 'react';
import * as ROSLIB from 'roslib';
import * as ROS3D from 'ros3d';

const MapViewer = () => {
  const viewerRef = useRef(null);

  useEffect(() => {
    const ros = new ROSLIB.Ros({
      url: 'ws://localhost:9090', // Adjust the URL based on your ROS server
    });

    const viewer = new ROS3D.Viewer({
      divID: 'map-viewer',
      width: 400,
      height: 400,
    });

    viewerRef.current = viewer;

    const gridClient = new ROS3D.OccupancyGridClient({
      ros,
      topic: '/map', // Adjust the topic based on your ROS map topic
      continuous: true,
      rootObject: viewer.scene,
    });

    gridClient.on('change', () => {
      viewer.scaleToDimensions(gridClient.currentGrid.width, gridClient.currentGrid.height);
    });

    return () => {
      // Cleanup resources on component unmount
      if (viewerRef.current) {
        viewerRef.current.shutdown();
      }
    };
  }, []);

  return <div id="map-viewer" style={{ width: '400px', height: '400px' }} />;
};

export default MapViewer;
