import React, { Component } from 'react';
import { Row, Col } from 'react-bootstrap';
import config from "../scripts/config";
import * as ROSLIB from 'roslib';



class RobotState extends Component {
  state={
    ros:null,
  };
constructor(){
  super();
  this.view_map=this.view_map.bind(this)
}

  init_connection() {
    this.state.ros = new ROSLIB.Ros();
    try {
      this.state.ros.connect('ws://' + config.ROSBRIDGE_SERVER_IP + ':9090');
      this.state.ros.on('connection', () => {
        console.log('Connected to ROS Bridge.');

         // Add map visualization setup
      });

      this.state.ros.on('close', () => {
        console.log('Connection to ROS Bridge closed.');
      });

      this.state.ros.on('error', (error) => {
        console.error('Error connecting to ROS Bridge:', error);
      });
    } catch (error) {
      console.error('Error connecting to ROS Bridge:', error);
    }
  }

  componentDidMount() {
    // Don't need to call getRobotState() here since it's already called in the 'connection' event
    this.init_connection();
    this.view_map();
  }
  view_map(){
    var viewer=new window.ROS2D.Viewer({
      divID:"nav_div",
      width:640,
      height:480,
    });
    var navClient = new window.NAV2D.OccupancyGridClientNav({
      ros: this.state.ros,
      rootObject: viewer.scene,
      viewer: viewer,
      serverName: "/move_base",  // Correct the typo here
      withOrientation: true,
    });

  }


  // ... your existing methods

  render() {
    return (
      <div>
        <div id="nav_div">Viewer</div>
      </div>
    );
  }
}

export default RobotState;
