import React, { Component } from 'react';
import { Row, Col } from 'react-bootstrap';
import Config from "../scripts/config";
import * as ROSLIB from 'roslib';



class RobotState extends Component {
  state={
    ros:null,
  };

  constructor(){
    super();
    this.state = {
      ros: new window.ROSLIB.Ros(),
      connection: false,
    };
    this.viewMap= this.view_Map.bind(this);
  }
  componentDidMount() {
    // Don't need to call getRobotState() here since it's already called in the 'connection' event
    this.initConnection();
    this.view_map();
    this.subscribeToRobotPose();
     // Subscribe to the /map topic
     const mapTopic = new window.ROSLIB.Topic({
      ros: this.state.ros,
      name: "/robot_pose_ekf/odom_combined",
      messageType: "geometry_msgs/PoseWithCovarianceStamped",
    });

    mapTopic.subscribe((message) => {
      // Log received map data to the console
      const pose = message.pose.pose;

      // Update state with the received robot position
      this.setState({ robotPosition: pose });
      console.log("Received Map Data: ", message);
      console.log("haare krishna");
    });
  }


  initConnection() {
    this.setState({ ros: new window.ROSLIB.Ros() });
    console.log("Map: " + this.state.ros);

    this.state.ros.on("error", (error) => {
      console.error("ROS Error:", error);
    });

    try {
      this.state.ros.connect(
        "ws://" +
          Config.ROSBRIDGE_SERVER_IP +
          ":" +
          Config.ROSBRIDGE_SERVER_PORT
      );
    } catch (error) {
      console.error(
        "ws://" +
          Config.ROSBRIDGE_SERVER_IP +
          ":" +
          Config.ROSBRIDGE_SERVER_PORT
      );
      console.error("Cannot connect to the WS robot. Try again after 1 second");
    }
  }



  viewMap() {
    const { ros } = this.state;

    if (ros) {
      const viewer = new window.ROS2D.Viewer({
        divID: 'nav_div3',
        width: 200,
        height: 200,
      });

      const arrow = new window.ROS2D.Arrow({
        size: 10,
        strokeSize: 1,
        fillColor: createjs.Graphics.getRGB(255, 0, 0),
      });

      // Add the arrow to the viewer's scene
      viewer.scene.addChild(arrow);

      const navClient = new window.ROS2D.OccupancyGridClientNav({
        ros,
        rootObject: viewer.scene,
        viewer,
        serverName: '/move_base',
        withOrientation: true,
      });

      // Update the arrow's position on each animation frame
      viewer.addObject(arrow);
      viewer.animate();

      viewer.on('frameRendered', () => {
        const { robotPosition, mapInfo } = this.state;

        if (robotPosition && mapInfo) {
          // Update arrow's position based on the robot position and map information
          const mapOrigin = mapInfo.origin.position;
          const robotPose = robotPosition.position;

          // Calculate the arrow's position relative to the map origin
          const arrowX = (robotPose.x - mapOrigin.x) / mapInfo.resolution;
          const arrowY = (robotPose.y - mapOrigin.y) / mapInfo.resolution;

          arrow.x = arrowX;
          arrow.y = arrowY;
          arrow.rotation = Math.atan2(robotPosition.orientation.z, robotPosition.orientation.w) * (180 / Math.PI);
        }
      });
    }
  }

  // ... your existing methods

  render() {
    return (
      <div>
        <div id="nav_div3"></div>
      </div>
    );
  }
}


export default RobotState;
